INSERT INTO `maincentertexts` (`id`, `title`, `description`, `is_active`) VALUES
(1, 'Чувство стиля', 'Салон по пошиву штор “Чувство стиля” давно и успешно работает в сфере текстильного оформления интерьеров. Основным направлением нашей деятельности является пошив штор на заказ.', 1),
(2, 'Наши услуги', 'Разнообразие и выбор услуг, которые мы можем предложить вам, охватывает полный цикл работ по текстильному дизайну, начиная с разработки дизайн-проекта и заканчивая монтажом готовых изделий..', 1),
(3, 'Качество', 'Мы создаем не просто шторы, которые будут закрывать вас и ваших близких от посторонних глаз, мы создаем произведение текстильного искусства, которое будет радовать своего владельца долгие годы.', 1);
