INSERT INTO `product` (`id`, `title`, `description`, `logo_id`, `sort`, `is_active`, `category_id`, `agent`, `agent_text`, `is_on_main`) VALUES
(1, 'Концертный зал филармонии', 'Выполнена огромная работа в зале. И еще немного описания по выполненным работам и пожеланиям заказчика, которые были исполнены в лучшем виде', NULL, 1, 1, 2, '', '', 0),
(2, 'Bookina', 'Выполнена огромная работа в зале. И еще немного описания по выполненным работам и пожеланиям заказчика, которые были исполнены в лучшем виде', 20, 2, 1, 2, 'Алена Грибова', 'Здравствуйте. Я заказывала оформление окон в гостиной. Выбрала плиссе. Дизайнер приехал ко мне и сделал все необходимые замеры. Меньше, чем через две недели произвели установку. Все идеально четко! Выражаю большую благодарность салону "Чувство стиля".', 1),
(3, 'Three leaf tech', 'Выполнена огромная работа в зале. И еще немного описания по выполненным работам и пожеланиям заказчика, которые были исполнены в лучшем виде', 27, NULL, 1, 2, 'Алена Грибова1', '1Здравствуйте. Я заказывала оформление окон в гостиной. Выбрала плиссе. Дизайнер приехал ко мне и сделал все необходимые замеры. Меньше, чем через две недели произвели установку. Все идеально четко! Выражаю большую благодарность салону "Чувство стиля".', 1);
